package Casting;// two types of casting Primitive and Non-Primitive.
                // Primitive - Implicit & Explicit.
                // Non-Primitive - Upcasting & Downcasting.

public class Cast1 { // Implicit casting. convert lower data to higher data

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		
		System.out.println(x);
		
		double d = x;
		
		System.out.println(d);

	}

}
